// function to update the changed table cells
function updateTableEdits(el){
  debugger;
  table = $(el).closest('table').DataTable();
  td = $(el).closest('td') || el;
  oldVal = table.cell( td ).data();
  newVal = el.value;
  if(oldVal === newVal ) 
      {return false;}
  else{
      table.cell( td ).data(newVal)
      searchConfig.orderableItems.response = table.data().toArray()
  }
}

// Check if value is valid JSON and has at least 1 entry
function is_json(value){
  try
  {
    if (JSON.parse(value).length > 0 ){return true;}
    else{return false;}
  }
  catch(err)
  {
    return false;
  }
}

function is_integer(value){
  var reg = new RegExp('^\\d+$');
  if (reg.test(value) ){return true;}
  else{return false;}
}

