$('form').on('click', 'tbody button.removeRow',function(){
  // get the datatable object
  var table = $(this.closest('table')).DataTable();
  // remove the current row
  table.row( $(this).parents('tr') )
  	.remove().draw();
  // set data to new contents of the table
  searchConfig.orderableItems.response = table.data().toArray();
