// ***** CONFIGURE *********
var load = {
	//Form to Load
  "formSlug":"requested-for-search", 
  //Container for the form (jQuery syntax)
  "container": '#requestedFor', 
  //Define values to pass into subform.  Map subform Field to value or K('field') reference.
	//NOTE: INCLUDE THESE FIELDS IN SUBFORM, OR PAGE WILL NOT LOAD
  "paramsInput": {
    "Requested For Display Name": K('field[Requested For Display Name]').value(),
    "Review Mode": K('form').reviewMode() ? true : "",
    "Request For": K('field[Requested For]').value() != null && K('identity').username != K('field[Requested For]').value()? "Someone Else" : "Me",
    "Email": K('field[Requested For Email]').value(),
    "Phone": K('field[Requested For Phone]').value(),
    "Company":  K('field[Requested For Company]').value(),
    "Responsible Company":K('field[Responsible Company]').value(),
	},
  //Define Fields to set on parent form using watch.  Map subform field to parent form field.
  "watchFields":{
    "Requested For Id": "Requested For",
    "Requested For Display Name": "Requested For Display Name",
    "Email": "Requested For Email",
    "Phone": "Requested For Phone",
    "Company":  "Requested For Company",
    "Responsible Company": "Responsible Company",
  }
}
// ***** END CONFIGURE *********

// Turn paramsInput into formatted/encoded urlParams array as that's what's required by the
// Kinetic App when passing in values
load.urlParams = [];
$.each( load.paramsInput, function( key, value ){
  load.urlParams.push( "values[" + encodeURIComponent( key ) + "]=" + encodeURIComponent( value ) );
})


// Remove null values from urlParams list
load.urlParams = load.urlParams.filter(function(val) { return val.split("=")[1]!=="null" });

// Load the Subform
K.load({
  path: bundle.spaceLocation() + '/'+ K("kapp").slug()+ '/' + load.formSlug + '?' + load.urlParams.join("&"),
  container: load.container,
  parent: K('form'),
  loaded: function(form) {
    // initialize watch fields definfed in configuration
		$.each(load.watchFields, function(source, desination){
		  form.$watch(source, function(newVal,oldVal){
        K('field['+desination+']').value(newVal);
      });
    })
  }
});

