            if(editableDisplay){
              data = data ? data : "";
              return '<input value = "' +data+'">';
            }
            else return data;
        }
      },
			{ 
        title:"Unit Type",
        name:"Description",
      },
      { 
        title:"Extra Cleaning",
        name:"Extra Cleaning",
        defaultContent: "", //Since Option is not in the bridge data is must have a default value
        // function to describe column display. Display Menu or empty cell.
        render: function ( data, type, row, meta ) {
          menu = row['Menu 1']; // get menu data from the "Menu 1" value which is not displayed
          // If cell has a value and table is in editable mode
          if(menu && menu != "" && editableDisplay){ 
            menuElements = typeof menuElements==="object"? menuElements : {}; // Start with all menus and values
            if (typeof menuElements[menu] == "undefined"){ // If the specific menu element has not already been created
              currentMenuValues = menuItems.filter(function(response){return response['Menu Name']==menu}) // Filter out value specific to configured menu
              // Create menu element
              var selectMenu = $('<select />')
                /*.append($("<option></option>")
