// Use these bridges (must be configured with these names in the forms Bridged Resources settings.
var bridges = ["Orderable Items","Orderable Items Menus"] 
// Initialize array for promises used to retrieve values from bridges.
var promises = []; 
// Function to retrieve data using Bridges.
loadBridge = function(bridge){
  return new Promise(function(resolve, reject){
    K('bridgedResource['+bridge+']').load({
      success: function(bridgedData) {
          resolve({"Bridge Name":bridge, "Response": bridgedData});
      }
    });
  })
}

// Iterate through each of the configured bridges.
bridges.forEach(function(bridge){
  promises.push(loadBridge(bridge))
})

// Initialize Objects
var orderableItems = {}, // for all orderable items
    menuItems = {}; // for all orderable items menus

// Use promises to ensure the orderable items and their menus are both retrieved  before building the editable table.
Promise.all(promises).then((values) => {
  // Filter out Orderable Items Menus
  menuItems = values.find(function(bridge){return bridge['Bridge Name']=="Orderable Items Menus"})['Response']
  // Filter out Orderable Items
  orderableItems = values.find(function(bridge){return bridge['Bridge Name']=="Orderable Items"})['Response']

  // If valid JSON is saved for the table already use it for display
  if(is_json(K('field[JSON Order Data]').value())){
    // Get values from JSON Order Data field
    searchConfig.orderableItems.response = JSON.parse(K('field[JSON Order Data]').value());
    // Load the Table with the values.
    DataViewer.renderResults( $(K('content[Orderable Items Container]').element()), searchConfig.orderableItems );
  }
  // Else the table is not editable.  Use values stored in the JSON Order Data field to display in Table.
  else{
    // Set the dataviewer response object to the orderable items bridge response if it has values
    searchConfig.orderableItems.response = typeof orderableItems == "object" && orderableItems.length > 0 ? orderableItems : [];
    // Load the Table with the orderable items values.
    DataViewer.renderResults( $(K('content[Orderable Items Container]').element()), searchConfig.orderableItems );   
  }
});

