$('form').on('change', 'tbody td select', function() {
  claculateCosts(this, {"Extra Cleaning":this.value});
})


// Bind event to calculate the cost of the item

$('form').on('change', 'tbody td input', function() {
  if( is_integer(this.value) ){
      claculateCosts(this, {"Quantity":this.value}); 
    }
  else {
    bundle.helpers.alert({
      "element":this,
      "message":"Quantity Must be an Integer.",
    });
    this.value = "";
  }
})  


