// Functions
*/

// function to update the changed table cells and calculate the costs
function claculateCosts(el, obj){
  var table = $(el).closest('table').DataTable(), // Get the table containing the elements
      row = $(el).closest('tr'), // Get the closest row
      rowData = table.row(row).data(), // Get data for the row.
      cleaning = (obj["Extra Cleaning"] == "Yes") || (!obj["Extra Cleaning"] && rowData['Extra Cleaning'] == "Yes") ? extraCleaningCost: 0 // Set "Extra Cleaning" cost if Extra Cleaning is Yes

  rowData['Quantity'] = obj["Quantity"] ? obj["Quantity"] : rowData['Quantity'];
  if(is_integer(rowData['Quantity'])){
    rowData['Extra Cleaning'] = obj["Extra Cleaning"] || rowData['Extra Cleaning'] || "No";
    rowData['Total'] = ( (cleaning * parseFloat(rowData['Quantity'])) + ( parseFloat(rowData['Cost']) * parseFloat(rowData['Quantity']) ) ).toFixed(2);

    table.row(row).data(rowData); // Update Row with new values
    searchConfig.orderableItems.response = table.data().toArray(); // Update the dataviewer response object to match the data in the table.
  }
}

// function to update the changed table cells
function updateTableEdits(el){
  table = $(el).closest('table').DataTable();
  td = $(el).closest('td') || el;
  oldVal = table.cell( td ).data();
  newVal = $(el).val() || $(el).text() || $(el).find('input').val();
  if(oldVal === newVal ) 
      {return false;}
  else{
      table.cell( td ).data(newVal)
      searchConfig.orderableItems.response = table.data().toArray()
  }
}

// Check if value is valid JSON and has at least 1 entry
function is_json(value){
  try
  {
    if (JSON.parse(value).length > 0 ){return true;}
    else{return false;}
  }
  catch(err)
  {
    return false;
  }
}

function is_integer(value){
  var reg = new RegExp('^\\d+$');
  if (reg.test(value) ){return true;}
  else{return false;}
}

